mapper.insert:
insert into plan(
	plan_no, plan_sender_no, plan_type, 
	plan_title, plan_content, 
	plan_start_time, plan_end_time
)
values (
	#{planNo}, #{planSenderNo}, #{planType},
	#{planTitle}, #{planContent},
	#{planStartTime}, #{planEndTime}
)

mapper.select:
select * from plan
where plan_sender_no = #{planSenderNo}
order by plan_create_time desc
